        return ""
    prefix = ""
    for i in range(len(strings)):
        for j in range(i+1, len(strings)):
            common_prefix = ""
            for k in range(min(len(strings[i]), len(strings[j]))):
                if strings[i][k] == strings[j][k]:
                    common_prefix += strings[i][k]
                else:
