def backtracking_sudoku(grid):
    rows, cols, boxes = zip(*grid)
    n = len(grid)

    def rec_backtrack(i, j):
        if i == n:
            return True
        if grid[i][j] == 0:
            for k in range(1, n+1):
                if rec_backtrack(i, j+1) or rec_backtrack(i+1, j):
                    return True
            grid[i][j] = k
            return rec_backtrack(i, j+1) or rec_backtrack(i+1, j)
        else:
            return False

    for i in range(n):
        for j in range(n):
            if grid[i][j] == 0:
                if not rec_backtrack(i, j):
                    print("No solution found")
                    return False

    print("Solution found:")
    for i in range(n):
        for j in range(n):
            print(grid[i][j], end=" ")
        print()

