    n = len(arr)
    for i in range(n//2):
        for j in range(0, n-i):
            temp = arr[j]
            k = j
            while k > 0 and temp < arr[k-1]:
                arr[k] = arr[k-1]
                k -= 1
            arr[k] = temp
    return arr

