def in_order_traversal(root):
if root == None:
return []
stack = [(root, [])]
while stack:
node, traverse_list = stack.pop()
yield node.val
if node.left:
stack.append((node.left, traverse_list + [node.val]))
if node.right:
stack.append((node.right, traverse_list + [node.val]))

def is_binary_search_tree(root):
if root == None:
return True
if not isinstance(root.val, int):
return False
if root.left and root.right:
return is_binary_search_tree(root.left) and is_binary_search_tree(root.right) and root.val > root.left.val > root.right.val
return False

def pre_order_traversal(root):
if root == None:
return []
yield root.val
if root.left:
yield from pre_order_traversal(root.left)
if root.right:
yield from pre_order_traversal(root.right)

def post_order_traversal(root):
if root == None:
return []
if root.left:
yield from post_order_traversal(root.left)
if root.right:
yield from post_order_traversal(root.right)
yield root.val

def is_height_balanced(root):
if root == None:
return True
if abs(height(root.left) - height(root.right)) > 1:
return False
return is_height_balanced(root.left) and is_height_balanced(root.right)

def max_value(root):
if root == None:
return -inf
if root.val > max_value(root.left):
return root.val
return max_value(root.left)

def min_value(root):
if root == None:
return inf
if root.val < min_value(root.left):
return root.val
return min_value(root.left)

def is_complete_binary_tree(root):
if root == None:
return True
if not root.left and not root.right:
return False
return is_complete_binary_tree(root.left) and is_complete_binary_tree(root.right)

def num_nodes(root):
if root == None:
return 0
return 1 + num_nodes(root.left) + num_nodes(root.right)

def level_order_traversal(root):
if root == None:
return []
queue = deque([root])
while queue:
node = queue.popleft()
yield node.val
if node.left:
queue.append(node.left)
if node.right:
queue.append(node.right)

